import PropTypes from "prop-types";
import React from "react";
import TrendingFlatRoundedIcon from "@mui/icons-material/TrendingFlatRounded";
import "./EventCategoryCard.css"

export default function EventCategoryCard({ details }) {
  return (
    <div className="rounded-[20px] h-[400px] overflow-hidden relative flex-[1_1_auto]">
    <div className="w-full h-full my-div" style={{backgroundImage:`url(${details.imageUrl})`}}/>
      <div className="z-10">
      <h1>{details?.header}</h1>
        <button type="button">
            More Info{" "}
            <TrendingFlatRoundedIcon className="transition-transform transform translate-x-0 group-hover:translate-x-2" />
        </button>
      </div>
    </div>
  );
}

EventCategoryCard.propTypes = {
  details: PropTypes.shape({
    image: PropTypes.object,
  }),
};
